import React from 'react'
import './Mainbody.css'
import heartbeat from '../assets/video1.mp4'
import '../assets/img1.jpeg'

window.requestAnimFrame = (function(){
  return  window.requestAnimationFrame       ||
          window.webkitRequestAnimationFrame ||
          window.mozRequestAnimationFrame    ||
          function( callback ){
            window.setTimeout(callback, 1000 / 60);
          };
})();

var canvas = document.getElementById('canvas'),
	context = canvas.getContext('2d'),
	width = canvas.width = document.body.offsetWidth,
	height = canvas.height = document.body.offsetHeight,
	ball = {
		x: 0,
		y: height / 2,
	},
	point = {
		x: 0,
		y: ball.y
	},
	current_point = 0;

var points = [
		{y:0,x:20},
		{y:0,x:1},
        {y:3,x:1},
	    {y:-10,x:2},
		{y:10,x:2},
	    {y:-12,x:3},
	    {y:35,x:5},
	    {y:-25,x:4},
	    {y:14,x:3},
	    {y:5,x:2},
		{y:0,x:1},
		{y:0,x:20}
	];

context.fillStyle = "rgba(255, 0, 0, 1)";
render();
function animateTo() {
	function dist(x1,x2,y1,y2) {
		var dx = x1 - x2,
			dy = y1 - y2;
		return {
			d: Math.sqrt(dx*dx + dy*dy),
			dx: dx,
			dy: dy
		};
	}
	var dis = dist(ball.x, point.x+points[current_point].x,ball.y, point.y+points[current_point].y);
	if( dis.d > 1 ) {
		var s = Math.abs(dis.dy) > 13 ? 2 : 1;
		ball.x += -( dis.dx / dis.d )*s;
		ball.y += -( dis.dy / dis.d )*s;
	} else {
		ball.x = point.x+points[current_point].x;
		ball.y = point.y+points[current_point].y;
		point.x += points[current_point].x;
		current_point++;
		if( current_point >= points.length || ball.x > width ) {
			current_point = 0;
			if( ball.x > width ) {
				point.x = ball.x = 0;
			}
		}
	}
}
function render() {
	requestAnimFrame(render);
	animateTo();
	context.fillStyle = "rgba(0, 0, 0, .01)";
	context.fillRect(0,0,width,height);
	context.fillStyle = "rgba(255, 0, 0, 1)";
	context.beginPath();
	context.arc(ball.x, ball.y, 1, 0, 2*Math.PI, true);
	context.closePath();
	context.fill();
}




const Mainbody = () => {
  return (
    <div className='main'>
      <canvas id="canvas"></canvas>
     <video src={heartbeat} autoPlay loop muted />
     <img src='img1.jpeg' />
    <div className='bodytitle'><span>Uwindsor</span>Healthify</div>
    <div className='bodydesc'>
     <p>Predict diseases with the help of AI  ðŸ¦¾  to save cost, time, and to prevent misdiagnosis. Any hospital or individual user can use this web application to predict the disease . Click on "Services" &  "Lifestyle" at the top ðŸ‘† to see the list of services.</p>
    </div>
    </div>
  )
}

export default Mainbody
